public class InventoryScanner {
    private static final Logger logger = LogManager.getLogger(InventoryScanner.class);
    public void scan_inventory(@NotNull String barcode) {
        Path basePath = Paths.get("/data/inventory_scans");
        Path targetPath;
        try {
            targetPath = basePath.resolve(Paths.get(barcode)).normalize();
            if (!targetPath.startsWith(basePath)) {
                throw new IllegalArgumentException("Invalid barcode input");
            }
            ProcessBuilder processBuilder = new ProcessBuilder("barcode_scanner", "--scan", targetPath.toString());
            processBuilder.redirectErrorStream(true);
            Process process = processBuilder.start();
            int exitCode = process.waitFor();
            if (exitCode != 0) {
                throw new IOException("Barcode scanning failed");
            }
        } catch (IOException | InterruptedException e) {
            logger.error("Error during inventory scan", e);
        }
    }
}

