public class IoTDeviceControl {
    private final Path deviceFilePath = Paths.get("/home/iot/devices/status");
    public void updateDeviceStatus(boolean newStatus) throws IOException {
        if (newStatus) {
            activateDevice();
        } else {
            deactivateDevice();
        }
    }
    private void activateDevice() throws IOException {
        Path tempFile = Files.createTempFile("deviceStatus", ".tmp");
        try (var writer = Files.newBufferedWriter(tempFile)) {
            writer.write("ACTIVE");
        }
        Files.move(tempFile, deviceFilePath, java.nio.file.StandardCopyOption.REPLACE_EXISTING);
    }
    private void deactivateDevice() throws IOException {
        Path tempFile = Files.createTempFile("deviceStatus", ".tmp");
        try (var writer = Files.newBufferedWriter(tempFile)) {
            writer.write("INACTIVE");
        }
        Files.move(tempFile, deviceFilePath, java.nio.file.StandardCopyOption.REPLACE_EXISTING);
    }
    public boolean isDeviceActive() throws IOException {
        return "ACTIVE".equals(new String(Files.readAllBytes(deviceFilePath)));
    }
    public static void main(String[] args) {
        IoTDeviceControl control = new IoTDeviceControl();
        try {
            control.updateDeviceStatus(true);
            System.out.println("Device is active: " + control.isDeviceActive());
            control.updateDeviceStatus(false);
            System.out.println("Device is active: " + control.isDeviceActive());
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

