public class SignalControlAccess {
    private final Set<UserRole> authorizedRoles = new HashSet<>();
    public SignalControlAccess() {
        authorizedRoles.add(UserRole.SYSTEM_ADMIN);
        authorizedRoles.add(UserRole.ENGINEER);
    }
    public boolean canAccessSignalControl(User user) {
        if (user == null || user.getRole() == null) {
            return false;
        }
        return authorizedRoles.contains(user.getRole());
    }
    public void authorizeUser(User user) {
        if (user != null && user.getRole() != null) {
            authorizedRoles.add(user.getRole());
        }
    }
    public void revokeUser(User user) {
        if (user != null && user.getRole() != null) {
            authorizedRoles.remove(user.getRole());
        }
    }
    public static class User {
        private String username;
        private UserRole role;
        public User(String username, UserRole role) {
            this.username = username;
            this.role = role;
        }
        public String getUsername() {
            return username;
        }
        public UserRole getRole() {
            return role;
        }
    }
    public enum UserRole {
        SYSTEM_ADMIN,
        ENGINEER,
        STAFF
    }
}

