public class EnergyConsumptionReportGenerator {
    private static final Path REPORTS_DIR = Paths.get("/var/energy/reports").toAbsolutePath().normalize();
    public void generateReport(String userId, String reportType) {
        if (!isValidReportType(reportType)) {
            throw new IllegalArgumentException("Invalid report type");
        }
        Path userDir = REPORTS_DIR.resolve(userId).toAbsolutePath().normalize();
        if (!isSafePath(userDir)) {
            throw new SecurityException("Invalid path");
        }
        Path reportPath = userDir.resolve(reportType + ".txt");
    }
    private boolean isSafePath(Path path) {
        try {
            Path realPath = path.toRealPath();
            return realPath.startsWith(REPORTS_DIR);
        } catch (Exception e) {
            return false;
        }
    }
    private boolean isValidReportType(String reportType) {
        return "daily".equals(reportType) || "weekly".equals(reportType) || "monthly".equals(reportType);
    }
}

