public class ActuarialDataProcessor {
    private static final Set<Class<?>> ALLOWED_CLASSES = Collections.unmodifiableSet(new HashSet<>(Arrays.asList(
            PolicyData.class,
            RiskAnalysis.class
    )));
    public void processSerializedData(String base64EncodedData) {
        try (ByteArrayInputStream bais = new ByteArrayInputStream(Base64.getDecoder().decode(base64EncodedData));
             ObjectInputStream ois = new ValidatingObjectInputStream(bais)) {
            Object obj = ois.readObject();
            if (obj instanceof PolicyData || obj instanceof RiskAnalysis) {
            } else {
                throw new IllegalArgumentException("Invalid object type");
            }
        } catch (IOException | ClassNotFoundException e) {
        }
    }
    private static class ValidatingObjectInputStream extends ObjectInputStream {
        public ValidatingObjectInputStream(InputStream in) throws IOException {
            super(in);
        }
        @Override
        protected Class<?> resolveClass(ObjectStreamClass desc) throws IOException, ClassNotFoundException {
            String className = desc.getName();
            for (Class<?> allowed : ALLOWED_CLASSES) {
                if (allowed.getName().equals(className)) {
                    return super.resolveClass(desc);
                }
            }
            throw new SecurityException("Unauthorized class deserialization attempt: " + className);
        }
    }
    static class PolicyData implements Serializable {
    }
    static class RiskAnalysis implements Serializable {
    }
}

