public class OrderService {
    private final OrderRepository orderRepository;
    public OrderService(OrderRepository orderRepository) {
        this.orderRepository = orderRepository;
    }
    public OrderStatus getOrderStatus(int orderId) throws SQLException {
        Connection connection = orderRepository.getConnection();
        try (PreparedStatement preparedStatement = connection.prepareStatement(
                "SELECT status FROM orders WHERE id = ?")) {
            preparedStatement.setInt(1, orderId);
            ResultSet resultSet = preparedStatement.executeQuery();
            if (resultSet.next()) {
                return OrderStatus.valueOf(resultSet.getString("status"));
            }
        } finally {
            orderRepository.closeConnection(connection);
        }
        return null;
    }
}

