public class ResearchPublicationController {
    private static final String ALLOWED_REDIRECT_DOMAIN = "https:
    @GetMapping("/publication/redirect")
    public String handleRedirect(@RequestParam(required = false) String redirectUrl, HttpServletResponse response) {
        if (redirectUrl == null || !isValidRedirect(redirectUrl)) {
            response.setStatus(HttpServletResponse.SC_BAD_REQUEST);
            return "error";
        }
        HttpHeaders headers = new HttpHeaders();
        headers.add("Location", redirectUrl);
        response.setStatus(HttpServletResponse.SC_MOVED_PERMANENTLY);
        return "redirect:" + redirectUrl;
    }
    private boolean isValidRedirect(String url) {
        if (url == null || !url.startsWith(ALLOWED_REDIRECT_DOMAIN)) {
            return false;
        }
        return true;
    }
}

