public class SecureDeviceConfigReader {
    private static final Set<String> ALLOWED_FILES = Set.of("settings.xml", "config.json");
    public void readConfigFile(String fileName) throws IOException {
        if (!ALLOWED_FILES.contains(fileName)) {
            throw new IllegalArgumentException("Invalid file name");
        }
        Path configPath = Paths.get("/etc/iot/device/config", fileName);
        Path realPath = configPath.toRealPath();
        Path normalizedPath = Paths.get("/etc/iot/device/config").relativize(realPath);
        if (!normalizedPath.startsWith(fileName)) {
            throw new SecurityException("Attempted Path Traversal");
        }
        try (FileInputStream fis = new FileInputStream(realPath.toFile())) {
            byte[] buffer = new byte[1024];
            fis.read(buffer);
        }
    }
}

public class FileDownloadService {
    private static final Path BASE_DIRECTORY = Paths.get("/secure/files");
    public Resource downloadFile(String requestedFilePath) throws IOException {
        Path resolvedPath = resolveRequestedPath(requestedFilePath);
        if (!resolvedPath.normalize().startsWith(BASE_DIRECTORY)) {
            throw new IOException("Invalid file path traversal attempt");
        }
        return loadFileAsResource(resolvedPath);
    }
    private Path resolveRequestedPath(String requestedFilePath) throws IOException {
        Path filePath = BASE_DIRECTORY.resolve(requestedFilePath);
        return Paths.get(filePath.toString()).toRealPath();
    }
    private Resource loadFileAsResource(Path filePath) throws IOException {
        if (Files.exists(filePath)) {
            return new UrlResource(filePath.toUri());
        } else {
            throw new IOException("File not found " + filePath);
        }
    }
}

public class FileDownloadServlet extends HttpServlet {
    private static final long serialVersionUID = 1L;
    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        String filePath = request.getParameter("filePath");
        if (filePath == null || !isValidFilePath(filePath)) {
            response.sendError(HttpServletResponse.SC_BAD_REQUEST, "Invalid file path");
            return;
        }
        Path normalizedPath = Paths.get(filePath).normalize();
        if (!isWithinAllowedDirectory(normalizedPath)) {
            response.sendError(HttpServletResponse.SC_FORBIDDEN, "Access denied");
            return;
        }
        try {
            System.out.println("Sending file: " + normalizedPath);
            response.setContentType("application/octet-stream");
        } catch (IOException e) {
            response.sendError(HttpServletResponse.SC_INTERNAL_SERVER_ERROR, "Failed to read file");
        }
    }
    private boolean isValidFilePath(String filePath) {
        return filePath.matches("^[a-zA-Z0-9._\\-/]+$");
    }
    private boolean isWithinAllowedDirectory(Path path) {
        try {
            Path allowedDirectory = Paths.get("/allowed/directory").toRealPath();
            Path targetPath = path.toRealPath();
            return targetPath.startsWith(allowedDirectory);
        } catch (IOException e) {
            return false;
        }
    }
}

