            return !path.isAbsolute() && path.startsWith("data/");
        } catch (IOException e) {
            return false;
        }
    }
    private static class SafeObjectInputStream extends ObjectInputStream {
        private final List<String> allowedClasses;
        public SafeObjectInputStream(java.io.InputStream in, List<String> allowedClasses) throws IOException {
            super(in);
            this.allowedClasses = allowedClasses;
        }
        @Override
        protected Class<?> resolveClass(ObjectStreamClass desc) throws IOException, ClassNotFoundException {
            String className = desc.getName();
            if (!allowedClasses.contains(className)) {
                throw new SecurityException("Unauthorized class deserialization attempt: " + className);
            }
            return super.resolveClass(desc);
        }
    }
}

