    }
}

public class InventoryDAO {
    private static final String GET_ITEM_QUERY = "SELECT itemId, itemName, quantity FROM inventory WHERE itemCategory = ?";
    public ResultSet getItemByCategory(String category) throws SQLException, NamingException {
        if (category == null || category.isEmpty()) {
            throw new IllegalArgumentException("Category cannot be null or empty");
        }
        Connection connection = null;
        PreparedStatement preparedStatement = null;
        ResultSet resultSet = null;
        try {
            Context initContext = new InitialContext();
            Context envContext  = (Context) initContext.lookup("java:/comp/env");
            DataSource ds = (DataSource) envContext.lookup("jdbc/InventoryDS");
            connection = ds.getConnection();
            preparedStatement = connection.prepareStatement(GET_ITEM_QUERY);
            preparedStatement.setString(1, category);
            resultSet = preparedStatement.executeQuery();
            return resultSet;
        } finally {
            if (resultSet != null) {
                try {
                    resultSet.close();
                } catch (SQLException e) {
                }
            }
            if (preparedStatement != null) {
                try {
                    preparedStatement.close();
                } catch (SQLException e) {
                }
            }
            if (connection != null) {
                try {
                    connection.close();
                } catch (SQLException e) {
                }
            }
        }
    }
}

public class AssetTransferService {
    @Autowired
    private DataSource dataSource;
    public void transferAsset(String sourceAddress, String destinationAddress, double amount) throws SQLException {
        if (!isValidAddress(sourceAddress) || !isValidAddress(destinationAddress)) {
            throw new IllegalArgumentException("Invalid address format");
        }
        String sql = "UPDATE wallet_balances SET balance = balance - ? WHERE address = ? AND balance >= ?";
        try (Connection conn = dataSource.getConnection();
             PreparedStatement stmt = conn.prepareStatement(sql)) {
            stmt.setDouble(1, amount);
            stmt.setString(2, sourceAddress);
            stmt.setDouble(3, amount);
            int updatedRows = stmt.executeUpdate();
            if (updatedRows > 0) {
                sql = "UPDATE wallet_balances SET balance = balance + ? WHERE address = ?";
                try (PreparedStatement stmt2 = conn.prepareStatement(sql)) {
                    stmt2.setDouble(1, amount);
                    stmt2.setString(2, destinationAddress);
                    stmt2.executeUpdate();
                }
            } else {
                throw new SQLException("Insufficient balance or invalid source address");
            }
        }
    }
    private boolean isValidAddress(String address) {
        return address.matches("^[a-fA-F0-9]{40}$");
    }
}

