public class TransactionLogger {
    private static final Logger logger = LoggerFactory.getLogger(TransactionLogger.class);
    public void logTransaction(String transactionId, String ipAddress) {
        if (isValidIpAddress(ipAddress)) {
            logger.info("Transaction {} from IP: {}", transactionId, sanitizeInput(ipAddress));
        } else {
            logger.error("Invalid IP address detected: {}", ipAddress);
        }
    }
    private boolean isValidIpAddress(String ipAddress) {
        String ipRegex = "^((25[0-5]|2[0-4][0-9]|[01]?\\d\\d?)\\.){3}(25[0-5]|2[0-4][0-9]|[01]?\\d\\d?)$";
        return ipAddress.matches(ipRegex);
    }
    private String sanitizeInput(String input) {
        try {
            return URLDecoder.decode(input, "UTF-8").replace("\\", "");
        } catch (UnsupportedEncodingException e) {
            logger.error("Encoding exception: ", e);
            return "Unknown";
        }
    }
    public static void main(String[] args) {
        TransactionLogger logger = new TransactionLogger();
        logger.logTransaction("TX123456", "192.168.0.1");
        logger.logTransaction("TX789012", "malicious; rm -rf /"); 
    }
}

public class CloudOrchestrationLogger {
    private static final Logger logger = LoggerFactory.getLogger(CloudOrchestrationLogger.class);
    private static final String[] SAFE_LOG_PAYLOADS = {"Deployment started", "Deployment succeeded", "Deployment failed"};
    public void logEvent(String event) {
        if (isSafeLogMessage(event)) {
            logger.info(event);
        } else {
            logger.error("Attempt to log malicious input detected: {}", event);
            Sentry.captureMessage("Malicious log injection attempt", Level.ERROR);
        }
    }
    private boolean isSafeLogMessage(String message) {
        for (String safePayload : SAFE_LOG_PAYLOADS) {
            if (safePayload.equals(message)) {
                return true;
            }
        }
        return false;
    }
    public void logError(String errorMessage) {
        if (isSafeLogMessage(errorMessage)) {
            logger.error("Error: {}", errorMessage);
        } else {
            logger.error("Attempt to log malicious error message detected: {}", errorMessage);
            Sentry.captureMessage("Malicious log injection attempt", Level.ERROR);
        }
    }
    public void logSecurityEvent(String securityEvent) {
        if (isSafeLogMessage(securityEvent)) {
            logger.warn("Security Event: {}", securityEvent);
        } else {
            logger.error("Attempt to log malicious security event detected: {}", securityEvent);
            Sentry.captureMessage("Malicious log injection attempt", Level.ERROR);
        }
    }
}

public class CasinoEventLogger {
    private static final Logger logger = LoggerFactory.getLogger(CasinoEventLogger.class);
    private static final java.util.regex.Pattern SAFE_LOG_PATTERN = java.util.regex.Pattern.compile("^[a-zA-Z0-9\\s,;:!?.]*$");
    public void logEvent(String playerAction, String details) {
        if (!SAFE_LOG_PATTERN.matcher(playerAction).matches()) {
            logger.error("Invalid player action detected: {}", details);
        } else {
            StringBuilder logMessage = new StringBuilder();
            String[] parts = playerAction.split("\\s+");
            for (String part : parts) {
                if (SAFE_LOG_PATTERN.matcher(part).matches()) {
                    logMessage.append(part).append(" ");
                }
            }
            logger.info(logMessage.toString().trim(), details);
        }
    }
    public void logUserInput(String userInput) {
        if (!SAFE_LOG_PATTERN.matcher(userInput).matches()) {
            logger.warn("Suspicious input detected: {}", userInput);
        } else {
            logger.info("User Input: {}", userInput);
        }
    }
    public static void main(String[] args) {
        CasinoEventLogger loggerService = new CasinoEventLogger();
        loggerService.logEvent("Player bets 100 on Blackjack", "Transaction ID: 12345");
        loggerService.logUserInput("Good user input");
        loggerService.logEvent("Injection attempt: '; DROP TABLE users;", "Test Log Injection");
        loggerService.logUserInput("<script>alert('Malicious log entry');</script>");
        loggerService.logEvent("SQL Injection attempt: SELECT * FROM users WHERE username='admin';", "Injection Test");
    }
}

