public class TradeFileProcessor {
    private static final Path SECCS_TRADES_DIR = FileSystems.getDefault().getPath("/opt/seccs/trades");
    public void processTradeFile(String filePath) {
        try {
            Path userFilePath = FileSystems.getDefault().getPath(filePath);
            Path fileToProcess = SECCS_TRADES_DIR.resolve(userFilePath.getFileName());
            fileToProcess = fileToProcess.toRealPath();
            if (!fileToProcess.startsWith(SECCS_TRADES_DIR)) {
                throw new SecurityException("Attempt to process file outside of allowed directory");
            }
            Path tempFile = SECCS_TRADES_DIR.resolve("temp_" + userFilePath.getFileName());
            Files.copy(userFilePath, tempFile, StandardCopyOption.ATOMIC_MOVE, StandardCopyOption.REPLACE_EXISTING);
            processFile(tempFile);
            Path processedDir = SECCS_TRADES_DIR.resolve("processed");
            Files.createDirectories(processedDir);
            Path finalFilePath = processedDir.resolve(userFilePath.getFileName());
            Files.move(tempFile, finalFilePath, StandardCopyOption.REPLACE_EXISTING);
        } catch (IOException | SecurityException e) {
            System.err.println("Error processing trade file: " + e.getMessage());
        }
    }
    private void processFile(Path filePath) {
        System.out.println("Processing file: " + filePath);
    }
}

