public class MilLogisticsSystem {
    private static final String[] ALLOWED_CLASSES = {
        "mil.logistics.inventory.Item",
        "java.lang.String"
    };
    public static void main(String[] args) {
        try {
            String serializedItem = "MIIB..."; 
            byte[] bytes = Base64.getDecoder().decode(serializedItem);
            ObjectInputFilter filter = createObjectInputFilter();
            System.out.println("Deserialized object: " + deserializeObject(bytes, filter));
        } catch (Exception e) {
            System.err.println("Error during deserialization: " + e.getMessage());
        }
    }
    private static ObjectInputFilter createObjectInputFilter() {
        return filterInfo -> {
            String className = filterInfo.className();
            if (className == null) {
                return ObjectInputFilter.Status.UNDECIDED;
            }
            for (String allowedClass : ALLOWED_CLASSES) {
                if (className.equals(allowedClass)) {
                    return ObjectInputFilter.Status.ALLOWED;
                }
            }
            return ObjectInputFilter.Status.REJECTED;
        };
    }
    private static Serializable deserializeObject(byte[] bytes, ObjectInputFilter filter) {
        return (Serializable) new Object(); 
    }
    public static class Item implements Serializable {
        private String name;
        private int quantity;
    }
}

