public class EmergencyResponseDispatcher {
    private static final Path ALLOWED_SCRIPT_DIR = Paths.get("/usr/local/emergency/scripts");
    @PostConstruct
    public void initialize() throws IOException {
        if (!Files.exists(ALLOWED_SCRIPT_DIR) || !Files.isDirectory(ALLOWED_SCRIPT_DIR)) {
            throw new IllegalStateException("Allowed script directory does not exist or is not a directory.");
        }
    }
    public void dispatchEmergencyResponse(String scriptName) throws IOException {
        Path scriptPath = ALLOWED_SCRIPT_DIR.resolve(scriptName).normalize();
        if (!scriptPath.startsWith(ALLOWED_SCRIPT_DIR)) {
            throw new IllegalArgumentException("Invalid script name.");
        }
        if (!Files.exists(scriptPath) || !Files.isRegularFile(scriptPath)) {
            throw new IOException("Script does not exist or is not a regular file.");
        }
        if (!Files.isExecutable(scriptPath)) {
            throw new IOException("Script is not executable.");
        }
        ProcessBuilder processBuilder = new ProcessBuilder(Collections.singletonList(scriptPath.toString()));
        processBuilder.redirectErrorStream(true);
        try {
            Process process = processBuilder.start();
            int exitCode = process.waitFor();
            System.out.println("Script executed with exit code: " + exitCode);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            throw new RuntimeException("Script execution was interrupted.", e);
        }
    }
    public static void main(String[] args) {
        EmergencyResponseDispatcher dispatcher = new EmergencyResponseDispatcher();
        try {
            dispatcher.dispatchEmergencyResponse("alert.sh");
        } catch (IOException e) {
            System.err.println("Failed to dispatch emergency response: " + e.getMessage());
        }
    }
}

