public class CommandLoggingService {
    private static final Logger logger = LoggerFactory.getLogger(CommandLoggingService.class);
    private static final List<String> SAFE_COMMANDS = Arrays.asList("RECEIVE", "TRANSMIT", "STATUS");
    public void executeCommand(String command) {
        if (!SAFE_COMMANDS.contains(command)) {
            logger.warn("Attempt to execute unsafe command: {}", StringEscapeUtils.escapeJava(command));
            throw new IllegalArgumentException("Invalid command");
        }
        try {
            switch (command) {
                case "RECEIVE":
                    logger.info("Executing RECEIVE command");
                    break;
                case "TRANSMIT":
                    logger.info("Executing TRANSMIT command");
                    break;
                case "STATUS":
                    logger.info("Executing STATUS command");
                    break;
            }
        } catch (Exception e) {
            logger.error("Error executing command: {}", StringEscapeUtils.escapeJava(command), e);
        }
    }
}

