public class ResearchDataAccessServlet extends HttpServlet {
    private static final List<String> ALLOWED_REDIRECTS = List.of(
        "https:
        "https:
        "https:
    );
    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws IOException {
        String redirectUrl = request.getParameter("redirect");
        if (isAllowedRedirect(redirectUrl)) {
            response.sendRedirect(redirectUrl);
        } else {
            response.setStatus(HttpServletResponse.SC_FORBIDDEN);
        }
    }
    private boolean isAllowedRedirect(String redirectUrl) {
        if (redirectUrl == null || redirectUrl.isEmpty()) {
            return false;
        }
        try {
            URI uri = new URI(redirectUrl);
            String scheme = uri.getScheme().toLowerCase();
            if (!"http".equals(scheme) && !"https".equals(scheme)) {
                return false;
            }
            return ALLOWED_REDIRECTS.contains(redirectUrl);
        } catch (Exception e) {
            return false;
        }
    }
}

