public class CaseService {
    @Autowired
    private CaseRepository caseRepository;
    public List<Case> getAllCasesForUser(String userId) {
        String currentUserId = SecurityContextUtil.getCurrentUserId();
        if (!currentUserId.equals(userId)) {
            throw new UnauthorizedAccessException("Cannot access cases of other users");
        }
        return caseRepository.findByUserId(userId);
    }
    public Case getCaseById(String caseId, String userId) {
        Optional<Case> optionalCase = caseRepository.findById(caseId);
        if (optionalCase.isPresent()) {
            Case theCase = optionalCase.get();
            String currentUserId = SecurityContextUtil.getCurrentUserId();
            if (theCase.getUserId().equals(currentUserId)) {
                return theCase;
            }
        }
        throw new UnauthorizedAccessException("Access denied to requested case");
    }
    public Case saveCase(Case theCase) {
        String currentUserId = SecurityContextUtil.getCurrentUserId();
        if (theCase.getUserId().equals(currentUserId)) {
            return caseRepository.save(theCase);
        }
        throw new UnauthorizedAccessException("Cannot save cases for other users");
    }
    public void deleteCaseById(String caseId, String userId) {
        Optional<Case> optionalCase = caseRepository.findById(caseId);
        if (optionalCase.isPresent()) {
            Case theCase = optionalCase.get();
            String currentUserId = SecurityContextUtil.getCurrentUserId();
            if (theCase.getUserId().equals(currentUserId)) {
                caseRepository.deleteById(caseId);
            } else {
                throw new UnauthorizedAccessException("Cannot delete cases of other users");
            }
        } else {
            throw new ResourceNotFoundException("Case not found with ID: " + caseId);
        }
    }
}

