public class VideoStreamHandler extends HttpServlet {
    private static final String MEDIA_ROOT = "/var/media/videos/";
    protected void doGet(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {
        String videoPath = request.getParameter("video");
        if (videoPath == null) {
            response.sendError(HttpServletResponse.SC_BAD_REQUEST, "No video specified");
            return;
        }
        videoPath = URLDecoder.decode(videoPath, "UTF-8");
        File videoFile = new File(MEDIA_ROOT + videoPath);
        if (!videoFile.exists() || !videoFile.isFile()) {
            response.sendError(HttpServletResponse.SC_NOT_FOUND, "Video not found");
            return;
        }
        response.setContentType("video/mp4");
        FileInputStream fis = new FileInputStream(videoFile);
        byte[] buffer = new byte[4096];
        int bytesRead;
        while ((bytesRead = fis.read(buffer)) != -1) {
            response.getOutputStream().write(buffer, 0, bytesRead);
        }
        fis.close();
    }
}

