public class RedirectManager {
    private final List<String> allowedHosts = List.of("https:
    public void handleRedirect(HttpServletRequest request, HttpServletResponse response) throws IOException {
        String redirectUrl = request.getParameter("url");
        if (redirectUrl != null) {
            if (isSafeRedirect(redirectUrl)) {
                response.sendRedirect(redirectUrl);
            } else {
                response.sendError(HttpServletResponse.SC_BAD_REQUEST, "Invalid redirect URL");
            }
        } else {
            response.sendError(HttpServletResponse.SC_BAD_REQUEST, "No redirect URL provided");
        }
    }
    private boolean isSafeRedirect(String url) {
        try {
            URL u = new URL(url);
            String protocol = u.getProtocol();
            String host = u.getHost();
            if (!"http".equals(protocol) && !"https".equals(protocol)) {
                return false;
            }
            return allowedHosts.stream().anyMatch(allowed -> host.endsWith(allowed));
        } catch (Exception e) {
            return false;
        }
    }
}

