public class LegalDocumentProcessor {
    public void processDocument(String filePath, String externalCommand) {
        try {
            if (filePath == null || !isValidPath(filePath)) {
                throw new IllegalArgumentException("Invalid file path");
            }
            ProcessBuilder processBuilder = new ProcessBuilder(externalCommand, filePath);
            Process process = processBuilder.start();
            BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
            String line;
            while ((line = reader.readLine()) != null) {
                System.out.println(line);
            }
            int exitCode = process.waitFor();
            System.out.println("Process exited with code " + exitCode);
        } catch (Exception e) {
            System.err.println("Error processing document: " + e.getMessage());
        }
    }
    private boolean isValidPath(String path) {
        return path != null && !path.contains("..");
    }
    public static void main(String[] args) {
        LegalDocumentProcessor processor = new LegalDocumentProcessor();
        String filePath = "path/to/document.txt";
        String externalCommand = "echo"; 
        processor.processDocument(filePath, externalCommand);
    }
}

