public class NetworkConfigurator {
    private static final Logger logger = LogManager.getLogger(NetworkConfigurator.class);
    public void configureNetwork(String networkParameters) {
        try {
            ProcessBuilder processBuilder = new ProcessBuilder("configNetwork.sh", networkParameters);
            logger.info("Configuring network with parameters: " + networkParameters);
            Process process = processBuilder.start();
            BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
            String line;
            while ((line = reader.readLine()) != null) {
                logger.info(line);
            }
            int exitCode = process.waitFor();
            if (exitCode == 0) {
                logger.info("Network configuration successful.");
            } else {
                logger.error("Network configuration failed with exit code: " + exitCode);
            }
        } catch (Exception e) {
            logger.error("Failed to configure network: " + e.getMessage(), e);
        }
    }
}

