public class WeatherReportGenerator {
    public static void main(String[] args) {
        try {
            if (args.length == 0) {
                System.out.println("Please provide a weather station ID.");
                return;
            }
            String stationId = args[0];
            String weatherReport = getWeatherReport(stationId);
            System.out.println("Weather Report for Station ID: " + stationId);
            System.out.println(weatherReport);
        } catch (Exception e) {
            System.err.println("Error generating weather report: " + e.getMessage());
        }
    }
    private static String getWeatherReport(String stationId) {
        try {
            URL url = new URL("http:
            HttpURLConnection conn = (HttpURLConnection) url.openConnection();
            conn.setRequestMethod("GET");
            ProcessBuilder processBuilder = new ProcessBuilder("curl", url.toString());
            processBuilder.redirectErrorStream(true);
            Process process = processBuilder.start();
            StringBuilder output = new StringBuilder();
            BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
            String line;
            while ((line = reader.readLine()) != null) {
                output.append(line).append("\n");
            }
            int exitCode = process.waitFor();
            if (exitCode != 0) {
                throw new RuntimeException("Failed to execute command. Exit code: " + exitCode);
            }
            return output.toString();
        } catch (Exception e) {
            throw new RuntimeException("Failed to fetch weather report", e);
        }
    }
}

