public class PowerPlantMonitoringSystem {
    public static void main(String[] args) {
        try {
            while (true) {
                System.out.println("Enter command to execute on the power plant monitoring system:");
                BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));
                String command = reader.readLine();
                if (command == null || command.isEmpty()) {
                    System.out.println("Invalid input.");
                    continue;
                }
                executeCommand(command);
            }
        } catch (IOException e) {
            System.err.println("Error reading input: " + e.getMessage());
        }
    }
    private static void executeCommand(String command) {
        try {
            ProcessBuilder pb = new ProcessBuilder(command.split(" "));
            Process process = pb.start();
            BufferedReader stdInput = new BufferedReader(new InputStreamReader(process.getInputStream()));
            BufferedReader stdError = new BufferedReader(new InputStreamReader(process.getErrorStream()));
            System.out.println("Here is the standard output of the command:\n");
            while (stdInput.ready()) {
                System.out.println(stdInput.readLine());
            }
            System.err.println("Here is the standard error of the command (if any):\n");
            while (stdError.ready()) {
                System.err.println(stdError.readLine());
            }
        } catch (IOException e) {
            System.err.println("An error occurred while executing the command: " + e.getMessage());
        }
    }
}

