public class TelemetryDataProcessor {
    public void processTelemetry(String command) {
        try {
            if (!isValidCommand(command)) {
                throw new IllegalArgumentException("Invalid command");
            }
            Process process = new ProcessBuilder("cmd", "/c", command).start();
            int exitCode = process.waitFor();
            if (exitCode != 0) {
                throw new IOException("Command execution failed with exit code " + exitCode);
            }
        } catch (IOException | InterruptedException e) {
            logger.error("Error processing telemetry command: " + e.getMessage());
        }
    }
    private boolean isValidCommand(String command) {
        return command.matches("[a-zA-Z0-9\\s]+");
    }
    private static final Logger logger = new Logger();
}

