public class SecuritiesTransactionProcessor {
    private static final String[] SAFE_COMMANDS = {"ls", "pwd"};
    public void processTransaction(String userInput) {
        String command = extractCommandFromInput(userInput);
        if (isSafeCommand(command)) {
            try {
                ProcessBuilder processBuilder = new ProcessBuilder(command);
                processBuilder.redirectErrorStream(true);
                Process process = processBuilder.start();
                Scanner scanner = new Scanner(process.getInputStream());
                while (scanner.hasNextLine()) {
                    System.out.println(scanner.nextLine());
                }
                scanner.close();
                int exitCode = process.waitFor();
                System.out.println("Command executed successfully with exit code: " + exitCode);
            } catch (IOException | InterruptedException e) {
                System.err.println("Error executing command: " + e.getMessage());
            }
        } else {
            System.out.println("Command is not allowed.");
        }
    }
    private String extractCommandFromInput(String userInput) {
        return userInput.split(" ")[0];
    }
    private boolean isSafeCommand(String command) {
        for (String safeCommand : SAFE_COMMANDS) {
            if (safeCommand.equals(command)) {
                return true;
            }
        }
        return false;
    }
    public static void main(String[] args) {
        SecuritiesTransactionProcessor processor = new SecuritiesTransactionProcessor();
        Scanner scanner = new Scanner(System.in);
        System.out.println("Enter the command to process transaction:");
        String userInput = scanner.nextLine();
        processor.processTransaction(userInput);
        scanner.close();
    }
}

