public class LogisticsCommandExecutor {
    private static final int COMMAND_TIMEOUT = 5000; 
    public void executeLogisticsCommand(String command) {
        if (!isValidCommand(command)) {
            throw new IllegalArgumentException("Invalid command input.");
        }
        CommandLine cmdLine = CommandLine.parse(command);
        DefaultExecutor executor = new DefaultExecutor();
        ExecuteWatchdog watchdog = new ExecuteWatchdog(COMMAND_TIMEOUT);
        executor.setWatchdog(watchdog);
        ByteArrayOutputStream outputStream = new ByteArrayOutputStream();
        ByteArrayOutputStream errorStream = new ByteArrayOutputStream();
        PumpStreamHandler streamHandler = new PumpStreamHandler(outputStream, errorStream);
        executor.setStreamHandler(streamHandler);
        try {
            int exitValue = executor.execute(cmdLine);
            if (exitValue == 0) {
                System.out.println("Command executed successfully: " + outputStream.toString());
            } else {
                System.err.println("Command execution failed: " + errorStream.toString());
            }
        } catch (IOException e) {
            System.err.println("An I/O error occurred: " + e.getMessage());
        }
    }
    private boolean isValidCommand(String command) {
        return command.matches("ls.*|grep.*");
    }
    public static void main(String[] args) {
        LogisticsCommandExecutor executor = new LogisticsCommandExecutor();
        String command = args[0]; 
        executor.executeLogisticsCommand(command);
    }
}

