public class ReactorStatusCommand {
    private static final Logger logger = LoggerFactory.getLogger(ReactorStatusCommand.class);
    public void executeCommand(String command) {
        if (!isValidCommand(command)) {
            logger.error("Invalid command: {}", command);
            throw new IllegalArgumentException("Invalid command provided.");
        }
        try {
            ProcessBuilder processBuilder = new ProcessBuilder("sh", "-c", "echo " + command);
            Process process = processBuilder.start();
            int exitCode = process.waitFor();
            if (exitCode != 0) {
                logger.error("Command execution failed with exit code: {}", exitCode);
            } else {
                logger.info("Command executed successfully.");
            }
        } catch (IOException | InterruptedException e) {
            logger.error("Error executing command: {}", e.getMessage(), e);
        }
    }
    private boolean isValidCommand(String command) {
        if (command == null || command.isEmpty()) {
            return false;
        }
        if (command.matches(".*[;&|<>`\\(\\)].*")) {
            return false;
        }
        return true;
    }
    public static void main(String[] args) {
        ReactorStatusCommand commandExecutor = new ReactorStatusCommand();
        try {
            commandExecutor.executeCommand("safe-command");
        } catch (IllegalArgumentException e) {
            logger.error(e.getMessage());
        }
    }
}

