public class LegalCaseFileController {
    @GetMapping("/viewfile")
    public String viewFile(@RequestParam String fileName) {
        if (!isValidFileName(fileName)) {
            return "Invalid file name";
        }
        StringBuilder output = new StringBuilder();
        try {
            ProcessBuilder processBuilder = new ProcessBuilder("cat", fileName);
            processBuilder.redirectErrorStream(true);
            Process process = processBuilder.start();
            try (BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()))) {
                String line;
                while ((line = reader.readLine()) != null) {
                    output.append(line).append("\n");
                }
                process.waitFor();
            }
        } catch (IOException | InterruptedException e) {
            output.append("Error reading file: ").append(e.getMessage());
        }
        return output.toString();
    }
    private boolean isValidFileName(String fileName) {
        if (fileName == null || fileName.isEmpty()) {
            return false;
        }
        if (!fileName.matches("^[a-zA-Z0-9._-]+\\.txt$")) {
            return false;
        }
        String sanitizedPath = "/safe/storage/directory/" + fileName;
        return sanitizedPath.equals(new File(sanitizedPath).getCanonicalPath());
    }
}

