public class SecureCommandExecutor {
    private static final String[] SAFE_COMMANDS = {"ls", "pwd", "date"};
    public void executeCommand(String command) {
        if (isValidCommand(command)) {
            try {
                Process process = Runtime.getRuntime().exec(sanitizeCommand(command));
            } catch (Exception e) {
                System.err.println("Error executing command: " + e.getMessage());
            }
        } else {
            System.err.println("Invalid or malicious command attempted: " + command);
        }
    }
    private boolean isValidCommand(String command) {
        return Arrays.stream(SAFE_COMMANDS).anyMatch(command::startsWith);
    }
    private String sanitizeCommand(String command) {
        return StringUtils.normalizeSpace(command);
    }
    public static void main(String[] args) {
        SecureCommandExecutor executor = new SecureCommandExecutor();
        executor.executeCommand("ls -l");
        executor.executeCommand("rm -rf /");
    }
}

