        String ipAddress = scanner.nextLine();
        scanner.close();
        if (isValidIpAddress(ipAddress)) {
            try {
                Process process = Runtime.getRuntime().exec(commandPrefix + ipAddress);
            } catch (IOException e) {
                System.err.println("Error occurred while executing command: " + e.getMessage());
            }
        } else {
            System.out.println("Invalid IP address. Please enter a valid IP address.");
        }
    }
    private boolean isValidIpAddress(String ipAddress) {
        String regex = "^([0-9]{1,3}\\.){3}[0-9]{1,3}$";
        if (ipAddress.matches(regex)) {
            String[] parts = ipAddress.split("\\.");
            for (String part : parts) {
                int number = Integer.parseInt(part);
                if (number < 0 || number > 255) {
                    return false;
                }
            }
            return true;
        }
        return false;
    }
    public static void main(String[] args) {
        NetworkConfigurationManager manager = new NetworkConfigurationManager();
        manager.configureNetwork();
    }
}

