class SecureInventoryCommandHandler {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        System.out.println("Enter the command to execute:");
        String userInput = scanner.nextLine();
        try {
            secureExecuteCommand(userInput);
        } catch (IOException e) {
            System.err.println("Error executing command: " + e.getMessage());
        } finally {
            scanner.close();
        }
    }
    private static void secureExecuteCommand(String command) throws IOException {
        String[] allowedCommands = {"ls", "pwd", "date"};
        boolean isAllowed = false;
        for (String allowedCommand : allowedCommands) {
            if (command.startsWith(allowedCommand)) {
                isAllowed = true;
                break;
            }
        }
        if (!isAllowed) {
            System.err.println("Command not allowed.");
            return;
        }
        ProcessBuilder processBuilder = new ProcessBuilder(command.split(" "));
        processBuilder.redirectErrorStream(true);
        Process process = processBuilder.start();
        try (Scanner outputReader = new Scanner(process.getInputStream())) {
            while (outputReader.hasNextLine()) {
                System.out.println(outputReader.nextLine());
            }
        }
        int exitCode = process.waitFor();
        System.out.println("Command executed with exit code: " + exitCode);
    }
}

