    public static void main(String[] args) {
        try (Scanner scanner = new Scanner(System.in)) {
            System.out.println("Enter the temperature value to adjust:");
            String userInput = scanner.nextLine();
            if (!isNumeric(userInput)) {
                System.out.println("Invalid temperature value. Please enter a numeric value.");
                return;
            }
            String temperature = sanitizeInput(userInput);
            executeCommand(temperature);
        } catch (Exception e) {
            System.err.println("An error occurred while processing your request: " + e.getMessage());
        }
    }
    private static boolean isNumeric(String str) {
        try {
            Double.parseDouble(str);
            return true;
        } catch (NumberFormatException e) {
            return false;
        }
    }
    private static String sanitizeInput(String input) {
        return input.replaceAll("[^0-9.]", "");
    }
    private static void executeCommand(String temperature) {
        try {
            ProcessBuilder pb = new ProcessBuilder("set-temperature.sh", temperature);
            pb.redirectErrorStream(true);
            process = pb.start();
        } catch (IOException e) {
            System.err.println("Failed to set temperature: " + e.getMessage());
        }
    }
}

