        userAccessLevels.put("operator", "Limited");
        userAccessLevels.put("guest", "None");
    }
    public String getNetworkConfig(String userId, String configId) {
        if (userAccessLevels.get(userId) != null && !userAccessLevels.get(userId).equals("None")) {
            return "Configuration for " + configId;
        } else {
            return "Access denied";
        }
    }
