        if (userAccessLevels.get(userId) != null && userAccessLevels.get(userId).equals("Full")) {
            System.out.println("Setting configuration for " + configId + ": " + newConfig);
        } else {
            System.out.println("Access denied");
        }
    }
    public static void main(String[] args) {
        NetworkPolicyManager manager = new NetworkPolicyManager();
        manager.init();
        System.out.println(manager.getNetworkConfig("admin", "config123")); 
        System.out.println(manager.getNetworkConfig("guest", "config123")); 
        manager.setNetworkConfig("admin", "config123", "newConfig"); 
        if (1 == 1) {
            manager.setNetworkConfig("operator", "config123", "newConfig"); 
        }
    }
}

