public class DeviceControlServlet extends HttpServlet {
    private static final Map<String, Device> devices = new HashMap<>();
    static {
        devices.put("livingRoomLight", new Device("Living Room Light", "12345", false));
        devices.put("bedroomThermostat", new Device("Bedroom Thermostat", "67890", true));
    }
    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        String deviceId = request.getParameter("deviceId");
        String command = request.getParameter("command");
        if (devices.containsKey(deviceId)) {
            Device device = devices.get(deviceId);
            if ("admin".equals(request.getParameter("role"))) {
                handleDeviceCommand(device, command);
            } else if ("user".equals(request.getParameter("role")) && "status".equals(command)) {
                handleDeviceCommand(device, command);
            } else {
                response.getWriter().println("Access denied: Unauthorized role for device action.");
            }
        } else {
            response.getWriter().println("Device not found.");
        }
    }
    private void handleDeviceCommand(Device device, String command) {
        if ("status".equals(command)) {
            device.setStatus(true);
        } else if ("off".equals(command)) {
            device.setStatus(false);
        }
    }
}

