public class TelemetryController {
    private Map<String, VehicleStatus> vehicleStatusMap = new HashMap<>();
    public TelemetryController() {
        vehicleStatusMap.put("Vehicle123", new VehicleStatus("Active", 0, 70));
        vehicleStatusMap.put("Vehicle456", new VehicleStatus("Inactive", 1, 0));
    }
    @GetMapping("/status/{vehicleId}")
    public ResponseEntity<VehicleStatus> getVehicleStatus(@PathVariable String vehicleId) {
        VehicleStatus status = vehicleStatusMap.get(vehicleId);
        if (status != null) {
            return ResponseEntity.ok(status);
        } else {
            return ResponseEntity.notFound().build();
        }
    }
    @PutMapping("/status/{vehicleId}")
    public ResponseEntity<Void> updateVehicleStatus(@PathVariable String vehicleId, @RequestBody VehicleStatus newStatus) {
        if (vehicleStatusMap.containsKey(vehicleId)) {
            vehicleStatusMap.put(vehicleId, newStatus);
            return ResponseEntity.ok().build();
        } else {
            return ResponseEntity.notFound().build();
        }
    }
    private static class VehicleStatus {
        private String status;
        private int batteryLevel;
        private double speed;
        public VehicleStatus(String status, int batteryLevel, double speed) {
            this.status = status;
            this.batteryLevel = batteryLevel;
            this.speed = speed;
        }
        public String getStatus() {
            return status;
        }
        public void setStatus(String status) {
            this.status = status;
        }
        public int getBatteryLevel() {
            return batteryLevel;
        }
        public void setBatteryLevel(int batteryLevel) {
            this.batteryLevel = batteryLevel;
        }
        public double getSpeed() {
            return speed;
        }
        public void setSpeed(double speed) {
            this.speed = speed;
        }
    }
}

