class DeFiBridgeTransactionProcessor {
    private String userRole;
    public DeFiBridgeTransactionProcessor(String userRole) {
        this.userRole = userRole;
    }
    public void processTransaction(String transactionData) throws IOException {
        if ("admin".equals(userRole)) {
            ObjectMapper objectMapper = new ObjectMapper();
            Transaction transaction = objectMapper.readValue(transactionData, Transaction.class);
            System.out.println("Processing transaction for admin: " + transaction.getAmount());
        } else if ("user".equals(userRole)) {
            ObjectMapper objectMapper = new ObjectMapper();
            Transaction transaction = objectMapper.readValue(transactionData, Transaction.class);
            if (transaction.getAmount() < 10000) {
                System.out.println("Processing transaction for user: " + transaction.getAmount());
            } else {
                System.out.println("Transaction amount exceeds allowed limit for user role.");
            }
        } else {
            System.out.println("Unauthorized transaction attempt by user with role: " + userRole);
        }
    }
    public static class Transaction {
        private double amount;
        public double getAmount() {
            return amount;
        }
        public void setAmount(double amount) {
            this.amount = amount;
        }
    }
    public static void main(String[] args) {
        DeFiBridgeTransactionProcessor processor = new DeFiBridgeTransactionProcessor("user");
        try {
            String transactionData = "{\"amount\": 5000}";
            processor.processTransaction(transactionData);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

public class InventoryServlet extends HttpServlet {
    private static final long serialVersionUID = 1L;
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        String partNumber = request.getParameter("partnumber");
        if (partNumber == null || partNumber.isEmpty()) {
            response.sendError(HttpServletResponse.SC_BAD_REQUEST, "Part number is required");
            return;
        }
        if (!isValidUser(request)) {
            response.sendError(HttpServletResponse.SC_UNAUTHORIZED, "Access denied");
            return;
        }
        try {
            PartDetails partDetails = fetchPartDetails(partNumber);
            response.setContentType("application/json");
            response.getWriter().write(partDetails.toJson());
        } catch (Exception e) {
            response.sendError(HttpServletResponse.SC_INTERNAL_SERVER_ERROR, "Failed to fetch part details");
        }
    }
    private boolean isValidUser(HttpServletRequest request) {
        String role = (String) request.getSession().getAttribute("userRole");
        return "admin".equals(role);
    }
    private PartDetails fetchPartDetails(String partNumber) throws Exception {
        if ("12345".equals(partNumber)) {
            return new PartDetails("Widget", "A123", 50);
        }
        throw new Exception("Part not found");
    }
    private static class PartDetails {
        private String name;
        private String code;
        private int stock;
        public PartDetails(String name, String code, int stock) {
            this.name = name;
            this.code = code;
            this.stock = stock;
        }
        public String toJson() {
            return "{\"name\":\"" + name + "\", \"code\":\"" + code + "\", \"stock\":" + stock + "}";
        }
    }
}

