public class MilitaryInventoryManager {
    private static final List<String> restrictedItems = new ArrayList<>();
    private static boolean adminMode = false;
    static {
        restrictedItems.add("Nuclear Warheads");
        restrictedItems.add("Advanced Radar Systems");
    }
    public static void main(String[] args) {
        String item = "Nuclear Warheads";
        boolean hasAccess = checkAccess(item);
        if (hasAccess) {
            System.out.println("Access granted to item: " + item);
        } else {
            System.out.println("Access denied for item: " + item);
        }
    }
    public static boolean checkAccess(String item) {
        if (adminMode) {
            return true;
        }
        for (String restrictedItem : restrictedItems) {
            if (restrictedItem.equals(item)) {
                return false;
            }
        }
        return true;
    }
    public static void toggleAdminMode() {
        adminMode = !adminMode;
    }
}

