public class WellDataAccess {
    private Map<String, Double> wellProduction;
    public WellDataAccess() {
        wellProduction = new HashMap<>();
        wellProduction.put("Oil Well X23", 1000.5);
        wellProduction.put("Gas Well Y45", 500.2);
    }
    public void updateWellProduction(String wellId, double production) {
        if (wellId == null || wellId.isEmpty()) {
            throw new IllegalArgumentException("Well ID cannot be null or empty");
        }
        wellProduction.put(wellId, production);
        System.out.println("Updated production for well: " + wellId + " to " + production);
    }
    public double getWellProduction(String wellId) {
        if (wellId == null || wellId.isEmpty()) {
            throw new IllegalArgumentException("Well ID cannot be null or empty");
        }
        return wellProduction.getOrDefault(wellId, 0.0);
    }
    public static void main(String[] args) {
        WellDataAccess access = new WellDataAccess();
        access.updateWellProduction("Oil Well Z78", 1500.0);
        double production = access.getWellProduction("Oil Well Z78");
        System.out.println("Retrieved production: " + production);
    }
}

public class InsuranceClaimProcessor {
    private List<Policy> policies = new ArrayList<>();
    public InsuranceClaimProcessor() {
        policies.add(new Policy(101, "Policy 1", true));
        policies.add(new Policy(102, "Policy 2", false));
        policies.add(new Policy(103, "Policy 3", true));
    }
    public void processClaim( int policyId) {
        Policy policy = findPolicyById(policyId);
        if (policy == null) {
            System.out.println("Invalid policy ID.");
            return;
        }
        if (policy.isAuthorized()) {
            System.out.println("Processing claim for policy " + policy.getId());
        } else {
            System.out.println("Access denied. You are not authorized to process this claim.");
        }
    }
    public Policy findPolicyById(int policyId) {
        for (Policy policy: policies) {
            if (policy.getId() == policyId) {
                return policy;
            }
        }
        return null;
    }
}

public class HomeAutomationController extends HttpServlet {
    private static final long serialVersionUID = 1L;
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        String userId = request.getParameter("userId");
        String command = request.getParameter("command");
        if (userId == null || command == null) {
            response.setStatus(HttpServletResponse.SC_BAD_REQUEST);
            return;
        }
        try {
            executeCommand(userId, command);
        } catch (Exception e) {
            response.setStatus(HttpServletResponse.SC_INTERNAL_SERVER_ERROR);
            response.getWriter().write("An error occurred while processing your request.");
        }
    }
    private void executeCommand(String userId, String command) {
        switch (command) {
            case "TURN_ON_LIGHTS":
                turnOnLights();
                break;
            case "SET_TEMPERATURE":
                setTemperature(Integer.parseInt(request.getParameter("temperature")));
                break;
            case "LOCK_DOOR":
                lockDoor();
                break;
            default:
                throw new IllegalArgumentException("Unknown command");
        }
    }
    private void turnOnLights() {
        System.out.println("Lights turned on.");
    }
    private void setTemperature(int temperature) {
        System.out.println("Temperature set to " + temperature);
    }
    private void lockDoor() {
        System.out.println("Door locked.");
    }
}

