        public User(String username, List<String> roles) {
            this.username = username;
            this.roles = new ArrayList<>(roles);
        }
        public String getUsername() {
            return username;
        }
        public boolean hasRole(String role) {
            return roles.contains(role);
        }
    }
    static class Order {
        private String orderId;
        private String userId;
        private boolean isSuspended;
        public Order(String orderId, String userId) {
            this.orderId = orderId;
            this.userId = userId;
            this.isSuspended = false; 
        }
        public String getOrderId() {
            return orderId;
        }
        public String getUserId() {
            return userId;
        }
        public boolean isSuspended() {
            return isSuspended;
        }
        public void setSuspended(boolean suspended) {
            this.isSuspended = suspended;
        }
    }
    private List<Order> orders;
    public SemiconductorOrderProcessor() {
        this.orders = new ArrayList<>();
    }
    public void createOrder(String userId, String orderId) {
        orders.add(new Order(orderId, userId));
    }
    public void suspendOrder(String userId, String orderId) {
        for (Order order : orders) {
            if (order.getOrderId().equals(orderId)) {
                order.setSuspended(true);
            }
        }
    }
    public Order getOrderDetails(String userId, String orderId) {
        for (Order order : orders) {
            if (order.getOrderId().equals(orderId)) {
                return order;
            }
        }
        return null;
    }
    public static void main(String[] args) {
        SemiconductorOrderProcessor processor = new SemiconductorOrderProcessor();
        User currentUser = new User("alice", List.of("admin"));
        processor.createOrder(currentUser.getUsername(), "order123");
        if (currentUser.hasRole("admin")) {
            processor.suspendOrder(currentUser.getUsername(), "order123");
        }
        Order order = processor.getOrderDetails(currentUser.getUsername(), "order123");
        if (order != null) {
            System.out.println("Order ID: " + order.getOrderId() + ", Suspended: " + order.isSuspended());
        }
    }
}

