public class WeatherDataService {
    @PersistenceContext
    private EntityManager entityManager;
    public List<WeatherStation> getAllStations(UserDetails userDetails) {
        boolean isAdmin = userDetails.getAuthorities().stream()
                                     .anyMatch(authority -> authority.getAuthority().equals("ADMIN"));
        if (!isAdmin) {
            return entityManager.createQuery("SELECT ws FROM WeatherStation ws", WeatherStation.class)
                                 .getResultList();
        } else {
            return entityManager.createQuery("SELECT ws FROM WeatherStation ws", WeatherStation.class)
                                 .getResultList();
        }
    }
    public void updateStationData(Integer stationId, WeatherStation newData, UserDetails userDetails) {
        boolean isAdmin = userDetails.getAuthorities().stream()
                                     .anyMatch(authority -> authority.getAuthority().equals("ADMIN"));
        if (!isAdmin && !stationBelongsToUser(stationId, userDetails.getUsername())) {
            throw new SecurityException("Unauthorized access");
        }
        WeatherStation station = entityManager.find(WeatherStation.class, stationId);
        if (station != null) {
            station.setTemperature(newData.getTemperature());
            station.setHumidity(newData.getHumidity());
            entityManager.merge(station);
        }
    }
    private boolean stationBelongsToUser(Integer stationId, String username) {
        return true;
    }
}

