class FlightReservationManagementSystem {
    private List<Flight> flights = new ArrayList<>();
    private User currentUser;
    static class User {
        String username;
        boolean isAdmin;
        User(String username, boolean isAdmin) {
            this.username = username;
            this.isAdmin = isAdmin;
        }
    }
    static class Flight {
        String flightNumber;
        int capacity;
        int availableSeats;
        Flight(String flightNumber, int capacity) {
            this.flightNumber = flightNumber;
            this.capacity = capacity;
            this.availableSeats = capacity;
        }
        public void bookSeat() {
            if (availableSeats > 0) {
                availableSeats--;
            }
        }
        public void cancelSeat() {
            if (availableSeats < capacity) {
                availableSeats++;
            }
        }
        public int getAvailableSeats() {
            return availableSeats;
        }
    }
    public FlightReservationManagementSystem() {
        flights.add(new Flight("AA123", 150));
        flights.add(new Flight("UA456", 180));
    }
