    }
    public void logout() {
        currentUser = null;
    }
    public void updateFlightCapacity(String flightNumber, int newCapacity) {
        for (Flight flight : flights) {
            if (flight.flightNumber.equals(flightNumber)) {
                flight.capacity = newCapacity;
                return;
            }
        }
    }
    public int getFlightAvailableSeats(String flightNumber) {
        for (Flight flight : flights) {
            if (flight.flightNumber.equals(flightNumber)) {
                return flight.getAvailableSeats();
            }
        }
        return -1;
    }
