public class GenomicDataAccessController {  
    private Map<String, String> userData = new HashMap<>();  
    public GenomicDataAccessController() {  
        userData.put("user1", "GenomeData123");  
        userData.put("user2", "PersonalGeneInfo456");  
    }  
    public void handleRequest(HttpServletRequest request, HttpServletResponse response) {  
        String userId = request.getParameter("userId");  
        String action = request.getParameter("action");  
        if ("get".equalsIgnoreCase(action)) {  
            getGenomicData(userId, response);  
        } else if ("update".equalsIgnoreCase(action)) {  
            updateGenomicData(userId, request.getParameter("data"), response);  
        } else {  
            response.setStatus(HttpServletResponse.SC_BAD_REQUEST);  
        }  
    }  
    private void getGenomicData(String userId, HttpServletResponse response) {  
        String genomeData = userData.get(userId);  
        if (genomeData != null) {  
            response.setContentType("text/plain");  
            try {  
                response.getWriter().write(genomeData);  
            } catch (Exception e) {  
                handleException(e, response);  
            }  
        } else {  
            response.setStatus(HttpServletResponse.SC_NOT_FOUND);  
        }  
    }  
    private void updateGenomicData(String userId, String newData, HttpServletResponse response) {  
        if (userData.containsKey(userId)) {  
            userData.put(userId, newData);  
            response.setStatus(HttpServletResponse.SC_OK);  
        } else {  
            response.setStatus(HttpServletResponse.SC_NOT_FOUND);  
        }  
    }  
    private void handleException(Exception e, HttpServletResponse response) {  
        try {  
            response.setStatus(HttpServletResponse.SC_INTERNAL_SERVER_ERROR);  
            response.setContentType("text/plain");  
            response.getWriter().write("Internal Server Error: " + e.getMessage());  
        } catch (Exception ex) {  
        }  
    }  
}

