public class SecureTradeHandler {
    @GetMapping("/settleTrade")
    public String settleTrade(HttpServletRequest request, @RequestParam("tradeId") String tradeId) {
        if (!isUserAuthorized(request, tradeId)) {
            return "Unauthorized access detected. Settlement failed.";
        }
        return "Trade settled successfully for tradeId: " + tradeId;
    }
    @GetMapping("/cancelTrade")
    public String cancelTrade(HttpServletRequest request, @RequestParam("tradeId") String tradeId) {
        if (!isUserAuthorized(request, tradeId)) {
            return "Unauthorized access detected. Cancellation failed.";
        }
        return "Trade cancelled successfully for tradeId: " + tradeId;
    }
    private boolean isUserAuthorized(HttpServletRequest request, String tradeId) {
        Object userRoles = request.getSession().getAttribute("USER_ROLES");
        if (userRoles == null) {
            return false;
        }
        Map<String, Boolean> rolesMap = (Map<String, Boolean>) userRoles;
        boolean isOperator = rolesMap.getOrDefault("TRADE_OPERATOR", false);
        return isOperator;
    }
}

