public class FlightReservationService {
    private List<Flight> flights = new ArrayList<>();
    private User sessionUser;
    public FlightReservationService() {
        flights.add(new Flight(101, "New York", "Los Angeles"));
        flights.add(new Flight(102, "Los Angeles", "New York"));
    }
    public boolean authenticate(String username, String password) {
        if ("admin".equals(username) && "admin123".equals(password)) {
            sessionUser = new User("admin", Role.ADMIN);
            return true;
        } else if ("user".equals(username) && "user123".equals(password)) {
            sessionUser = new User("user", Role.USER);
            return true;
        }
        return false;
    }
    public List<Flight> searchFlights(String from, String to) {
        validateUser();
        List<Flight> filteredFlights = new ArrayList<>();
        for (Flight flight : flights) {
            if (flight.from.equals(from) && flight.to.equals(to)) {
                filteredFlights.add(flight);
            }
        }
        return filteredFlights;
    }
    public void bookFlight(int flightId) {
        validateUser();
        if (sessionUser.getRole() != Role.ADMIN) {
            throw new AccessControlException("Booking flights is restricted to admins.");
        }
        System.out.println("Flight " + flightId + " booked by admin.");
    }
    private void validateUser() {
        if (sessionUser == null) {
            throw new AccessControlException("User not authenticated.");
        }
    }
    public static class Flight {
        int id;
        String from;
        String to;
        public Flight(int id, String from, String to) {
            this.id = id;
            this.from = from;
            this.to = to;
        }
    }
    public static class User {
        String username;
        Role role;
        public User(String username, Role role) {
            this.username = username;
            this.role = role;
        }
        public Role getRole() {
            return role;
        }
    }
    public enum Role {
        ADMIN, USER
    }
    public static class AccessControlException extends RuntimeException {
        public AccessControlException(String message) {
            super(message);
        }
    }
    public static void main(String[] args) {
        FlightReservationService service = new FlightReservationService();
        service.authenticate("admin", "admin123");
        System.out.println(service.searchFlights("New York", "Los Angeles"));
        service.bookFlight(101);
        service.authenticate("user", "user123");
        System.out.println(service.searchFlights("Los Angeles", "New York"));
        try {
            service.bookFlight(102); 
        } catch (AccessControlException e) {
            System.out.println(e.getMessage());
        }
    }
}

public class PolicyGenerator {
    private Map<String, String> policyCache = new HashMap<>();
    private Set<String> allowedUsers = new HashSet<>(Arrays.asList("admin", "actuary123"));
    public String generatePolicy(HttpServletRequest request, HttpServletResponse response) {
        String user = request.getParameter("user");
        if (user == null || !allowedUsers.contains(user)) {
            response.setStatus(HttpServletResponse.SC_FORBIDDEN);
            return "Access denied";
        }
        String policyId = request.getParameter("policyId");
        if (policyId == null) {
            response.setStatus(HttpServletResponse.SC_BAD_REQUEST);
            return "Policy ID is required";
        }
        String cachedPolicy = policyCache.get(policyId);
        if (cachedPolicy != null) {
            return cachedPolicy;
        }
        String newPolicy = fetchPolicyFromDatabase(policyId);
        if (newPolicy != null) {
            policyCache.put(policyId, newPolicy);
            return newPolicy;
        } else {
            response.setStatus(HttpServletResponse.SC_NOT_FOUND);
            return "Policy not found";
        }
    }
    private String fetchPolicyFromDatabase(String policyId) {
        return "Policy for ID: " + policyId;
    }
}

