public class ReactorStatusController {
    private final ReactorDataService reactorDataService;
    private final UserAccessService userAccessService;
    public ReactorStatusController(ReactorDataService reactorDataService, UserAccessService userAccessService) {
        this.reactorDataService = reactorDataService;
        this.userAccessService = userAccessService;
    }
    @GetMapping("/reactor-status")
    public ResponseEntity<String> getReactorStatus(@RequestParam String reactorId, @RequestParam String userId) {
        if (!userAccessService.isUserAuthorized(userId)) {
            return new ResponseEntity<>("Unauthorized access", HttpStatus.FORBIDDEN);
        }
        List<String> reactorIdsAccessibleToUser = userAccessService.getReactorIds(userId);
        if (!reactorIdsAccessibleToUser.contains(reactorId)) {
            return new ResponseEntity<>("Reactor not accessible", HttpStatus.FORBIDDEN);
        }
        String reactorStatus = reactorDataService.getReactorStatus(reactorId);
        if (reactorStatus == null) {
            return new ResponseEntity<>("Reactor not found", HttpStatus.NOT_FOUND);
        }
        return new ResponseEntity<>(reactorStatus, HttpStatus.OK);
    }
}

