public class CasinoUserAccessControl {
    private List<String> authorizedUsers = new ArrayList<>();
    public CasinoUserAccessControl() {
        authorizedUsers.add("admin");
        authorizedUsers.add("moderator123");
    }
    public void handleUserRequest(HttpServletRequest request) throws IOException {
        String userRole = request.getParameter("role");
        if (isAuthorized(userRole)) {
            processUserRequest(request);
        } else {
            throw new SecurityException("Access denied: User does not have the required role");
        }
    }
    private boolean isAuthorized(String role) {
        return authorizedUsers.contains(role);
    }
    private void processUserRequest(HttpServletRequest request) throws IOException {
        String action = request.getParameter("action");
        if ("withdraw".equals(action)) {
            withdrawFunds(request);
        } else if ("deposit".equals(action)) {
            depositFunds(request);
        } else {
            throw new IllegalArgumentException("Invalid action specified");
        }
    }
    private void withdrawFunds(HttpServletRequest request) throws IOException {
        String amount = request.getParameter("amount");
        if (isValidAmount(amount)) {
            String userId = request.getParameter("userId");
            System.out.println("User " + userId + " withdrew " + amount);
        } else {
            throw new IllegalArgumentException("Invalid withdrawal amount");
        }
    }
    private void depositFunds(HttpServletRequest request) throws IOException {
        String amount = request.getParameter("amount");
        if (isValidAmount(amount)) {
            String userId = request.getParameter("userId");
            System.out.println("User " + userId + " deposited " + amount);
        } else {
            throw new IllegalArgumentException("Invalid deposit amount");
        }
    }
    private boolean isValidAmount(String amount) {
        try {
            double value = Double.parseDouble(amount);
            return value > 0;
        } catch (NumberFormatException e) {
            return false;
        }
    }
    public static void main(String[] args) {
        CasinoUserAccessControl accessControl = new CasinoUserAccessControl();
        try {
            HttpServletRequest mockRequest = createMockHttpServletRequest("admin", "withdraw", "100.0");
            accessControl.handleUserRequest(mockRequest);
        } catch (IOException | SecurityException e) {
            System.err.println(e.getMessage());
        }
    }
    private static HttpServletRequest createMockHttpServletRequest(String role, String action, String amount) {
        return new HttpServletRequest() {
            @Override
            public String getParameter(String name) {
                switch (name) {
                    case "role":
                        return role;
                    case "action":
                        return action;
                    case "amount":
                        return amount;
                    default:
                        return null;
                }
            }
            @Override
            public String getMethod() {
                return "POST";
            }
            @Override
            public String getRequestURI() {
                return "/userAction";
            }
        };
    }
