public class GeneDataAccessController {
    private static final String ADMIN_ROLE = "ADMIN";
    private static final String READ_ACCESS_PERMISSION = "READ";
    @GetMapping("/api/gene-data")
    public String fetchGeneData(HttpServletRequest request, @RequestParam("geneId") String geneId) {
        String[] roles = (String[]) request.getSession().getAttribute("userRoles");
        boolean isAdmin = false;
        for (String role : roles) {
            if (ADMIN_ROLE.equals(role)) {
                isAdmin = true;
                break;
            }
        }
        String[] permissions = (String[]) request.getSession().getAttribute("userPermissions");
        boolean hasReadPermission = false;
        for (String permission : permissions) {
            if (READ_ACCESS_PERMISSION.equals(permission)) {
                hasReadPermission = true;
                break;
            }
        }
        if (!isValidGeneId(geneId)) {
            return "Invalid gene ID.";
        }
        if (isAdmin || hasReadPermission) {
            try {
                return retrieveGeneData(geneId);
            } catch (Exception e) {
                return "Error retrieving gene data.";
