public class CasinoTransactionServlet extends HttpServlet {
    private static final long serialVersionUID = 1L;
    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws IOException {
        try {
            Principal user = request.getUserPrincipal();
            if (user == null) {
                response.setStatus(HttpServletResponse.SC_UNAUTHORIZED);
                return;
            }
            String userId = user.getName();
            String transactionId = request.getParameter("transactionId");
            if (transactionId == null || transactionId.isEmpty()) {
                response.setStatus(HttpServletResponse.SC_BAD_REQUEST);
                return;
            }
            boolean isAuthorized = checkAuthorization(userId, transactionId);
            if (!isAuthorized) {
                response.setStatus(HttpServletResponse.SC_FORBIDDEN);
                return;
            }
            processTransaction(transactionId);
            response.setStatus(HttpServletResponse.SC_OK);
        } catch (Exception e) {
            response.getWriter().println("An error occurred: " + e.getMessage());
            response.setStatus(HttpServletResponse.SC_INTERNAL_SERVER_ERROR);
        }
    }
    private boolean checkAuthorization(String userId, String transactionId) {
        List<String> authorizedTransactions = getAuthorizedTransactionsForUser(userId);
        return authorizedTransactions.contains(transactionId);
    }
    private List<String> getAuthorizedTransactionsForUser(String userId) {
        return List.of("transaction123", "transaction456");
    }
    private void processTransaction(String transactionId) {
        System.out.println("Processing transaction: " + transactionId);
    }
}

